mapper.select:
select board.*, co_name as bo_co_name from board JOIN community ON co_num = bo_co_num where
bo_title like concat('%', #{cri.search}, '%') or bo_content like concat('%', #{cri.search}, '%') or bo_me_id = #{cri.search}
bo_title like concat('%', #{cri.search}, '%') or bo_content like concat('%', #{cri.search}, '%')
bo_me_id = #{cri.search}
order by bo_num desc limit #{cri.pageStart}, #{cri.perPageNum}

select count(*) from board where
bo_title like concat('%', #{cri.search}, '%') or bo_content like concat('%', #{cri.search}, '%') or bo_me_id = #{cri.search}
bo_title like concat('%', #{cri.search}, '%') or bo_content like concat('%', #{cri.search}, '%')
bo_me_id = #{cri.search}
