mapper.select:
select product.*, mg_title as pr_mg_name, tg_title as pr_tg_name from product join midgroup on pr_mg_num = mg_num join topgroup on mg_tg_num = tg_num
where (pr_name like concat('%', #{cri.search}, '%') or pr_content like concat('%', #{cri.search}, '%')) and pr_place like concat('%', #{cri.place}, '%') and pr_price between #{cri.minPrice} and #{cri.maxPrice} and pr_mg_num = #{num}
where pr_price between #{cri.minPrice} and #{cri.maxPrice} and pr_place like concat('%', #{cri.place}, '%') and pr_mg_num = #{num}
and pr_ps_state != "판매완료"
order by pr_price desc
order by pr_price asc
order by pr_num desc
order by pr_view desc
order by pr_basket desc
limit #{cri.pageStart} , #{cri.perPageNum}

select count(*) from product join midgroup on pr_mg_num = mg_num 
                     join topgroup on mg_tg_num = tg_num 

   where (pr_name like concat('%', #{cri.search}, '%')
   or pr_content like concat('%', #{cri.search}, '%'))
   and pr_place like concat('%', #{cri.place}, '%')
   and pr_price between #{cri.minPrice} and #{cri.maxPrice}
   and  pr_mg_num = #{num}	


   where  pr_price between #{cri.minPrice} and #{cri.maxPrice}
   and  pr_place like concat('%', #{cri.place}, '%')
   and  pr_mg_num = #{num}


     and pr_ps_state != "판매완료"

select ifnull(max(pr_price),0) from product

      where (pr_name like concat('%', #{cri.search}, '%')
      or pr_content like concat('%', #{cri.search}, '%'))
      and pr_place like concat('%', #{cri.place}, '%')
      and pr_price between #{cri.minPrice} and #{cri.maxPrice}
      and  pr_mg_num = #{num}	


      where  pr_price between #{cri.minPrice} and #{cri.maxPrice}
      and  pr_place like concat('%', #{cri.place}, '%')
      and  pr_mg_num = #{num}


        and pr_ps_state != "판매완료"

select ifnull(min(pr_price),0) from product

     where (pr_name like concat('%', #{cri.search}, '%')
     or pr_content like concat('%', #{cri.search}, '%'))
     and pr_place like concat('%', #{cri.place}, '%')
     and pr_price between #{cri.minPrice} and #{cri.maxPrice}
     and  pr_mg_num = #{num}	


     where  pr_price between #{cri.minPrice} and #{cri.maxPrice}
     and  pr_place like concat('%', #{cri.place}, '%')
     and  pr_mg_num = #{num}


       and pr_ps_state != "판매완료"

select ifnull(avg(pr_price),0) from product

     where (pr_name like concat('%', #{cri.search}, '%')
     or pr_content like concat('%', #{cri.search}, '%'))
     and pr_place like concat('%', #{cri.place}, '%')
     and pr_price between #{cri.minPrice} and #{cri.maxPrice}
     and  pr_mg_num = #{num}	


     where  pr_price between #{cri.minPrice} and #{cri.maxPrice}
     and  pr_place like concat('%', #{cri.place}, '%')
     and  pr_mg_num = #{num}


       and pr_ps_state != "판매완료"
