mapper.select:
SELECT report.*, (SELECT me_report_count FROM member WHERE me_id = #{me_id}) AS me_report_count, (SELECT me_stop_date FROM member WHERE me_id = #{me_id}) AS me_stop_date FROM report
WHERE re_me_id LIKE CONCAT('%', #{crir.search}, '%')
ORDER BY ${crir.order} DESC LIMIT #{crir.pageStart}, #{crir.perPageNum}
